	    while (line) {
	      var blocks = [];
	      while (line && line !== 'NL') {
	        var b = pLine();

	        if (b.isElse || b.isEnd) {
	          b = new Block(extend(b.info, {
	            shape: 'stack',
	          }), b.children);
	        }

	        if (b.isHat) {
	          if (blocks.length) scripts.push(new Script(blocks));
	          blocks = [b];
	        } else if (b.isFinal) {
	          blocks.push(b);
	          break;
	        } else if (b.isCommand) {
	          blocks.push(b);
	        } else { // reporter or predicate
	          if (blocks.length) scripts.push(new Script(blocks));
	          scripts.push(new Script([b]));
	          blocks = [];
	          break;
	        }
	      }
	      if (blocks.length) scripts.push(new Script(blocks));
	      while (line === 'NL') next();
	    }
